import { useState } from 'react';
import Layout from '../components/Layout';
import styled from 'styled-components';
import Carousel from 'react-multi-carousel';

const responsive = {
  superLargeDesktop: {
    // the naming can be any, depends on you.
    breakpoint: { max: 4000, min: 3000 },
    items: 5
  },
  desktop: {
    breakpoint: { max: 3000, min: 1024 },
    items: 3
  },
  tablet: {
    breakpoint: { max: 1024, min: 601 },
    items: 3
  },
  mobile: {
    breakpoint: { max: 600, min: 0 },
    items: 1
  }
};
export const MainWrapper = styled.div`
  padding: 0px 0px 0px 0px;
  box-sizing: border-box;
  
`;
export const IndexWrapper = styled.div`
  padding:10% 8% 2% 8%;
  background: #f1f1f1;
  heigth:120vh;
  min-height:120vh;
`;
export const Titleh1 = styled.h1`
  font-family:'Open Sans', sans-serif;
`;
export const PortadaTurismo = styled.div`
  padding:14px;
  border-radius:6px;
  width:80vw;
  position:relative;
  background:#ebebeb;
  height:40vh;
`;
export const Title = styled.h2`
  font-family:'Open Sans', sans-serif;
  color: ${props => props.color ? props.color : '#a1a1a1'};
  font-size:1.8rem;
  position:relative;
`;
export const PortadaWrapper = styled.div`
  display:grid;
  justify-content:center;
`;
export const PTag = styled.p`
font-family:'Open Sans', sans-serif;
line-height: ${props => props.line ? props.line : '1.2'};
font-size: ${props => props.size ? props.size : '0.8rem'};
color: ${props => props.color ? props.color : '#333'};
font-weight: ${props => props.weight ? props.weight : '500'};
`;
export const CarouselWrapp = styled.div`
padding: 5vh 0 0 0;
display:grid;
width:100%;
height:75vh;
grid-template-columns:25% 1fr;
grid-column-gap:1vw;
`;
export const Present = styled.div`
  background:#e4e4e4;
  width:100%;
  height:100%;
  padding:2vw;
`;
export const BtnIntro = styled.a`
background:#fd2542;
padding:12px;
border-radius:5px;
color:#f1f1f1;
font-family:'Open Sans', sans-serif;
font-weight:600;
`;
export const Card = styled.div`
width:100%;
height:100%;
background:#f1f1f1;
box-shadow:  5px 5px 10px #b0b0b0, 
             -5px -5px 10px #ffffff;
`;
export const CardImg = styled.div`
  width:100%;
  height:65vh;
  background-image: url('${props => props.image}');
  background-position:center;
  background-size:cover;
  position:relative;
`;
export const NochesCard = styled.p`
background: #ea1b37;
margin: 0;
color: #fff;
position: absolute;
font-size: 0.6rem;
left: 1vw;
line-height: 1.5;
bottom: -1vh;
font-weight: 600;
height: 3vh;
padding: 0.3vw;
border-radius: 5px;
`;
export const TitleCard = styled.h4`
  color: #333;
`;
export const PriceCard = styled.p`
  color:#333;
`;
export const DiscountCard = styled.p`
  background: green;
  color:white;
`;
export const CardInfoWrap = styled.div`
  padding:10px;
  display:grid;
`;
export const CardTop = styled.div`
  
`;
export const CardBottom = styled.div`
  
`;

const Index = () => {

    const packages = [
      {
        title: 'Corredor del Bermejo',
        picture : 'v1.jpg',
        nights: '3 NOCHES',
        price: 12300,
        dct: '-6%'
      },
      {
        title: 'Corredor Ruta 40',
        picture : 'v2.jpg',
        nights: '3 NOCHES',
        price: 9200,
        dct: '-20%'
      },
      {
        title: 'Corredor de la Costa',
        picture : 'v3.jpg',
        nights: '7 NOCHES',
        price: 96300,
        dct: '-27%'
      },
      {
        title: 'Corredor del CaÃ±on',
        picture : 'v4.jpg',
        nights: '3 NOCHES',
        price: 6300,
        dct: '-15%'
      }

    ]
    const images = ['v1.jpg', 'v2.jpg', 'v3.jpg', 'v4.jpg'];
 
    return ( 
        <MainWrapper>
            <Layout>
                <IndexWrapper>
                    <PortadaWrapper>
                        <PortadaTurismo>
                            <Title> Paquetes turisticos</Title>
                            <PTag weight="600"> Encuentra los mejores recorridos para conocer lugares mÃ¡gicos e inolvidables de nuestra provincia.</PTag>
                            <PTag weight="600"> En el siguiente link podrÃ¡s encontrar una gran variedad de paquetes de hoteles, recorridos, confiterÃ­as y mÃ¡s!</PTag>
                        </PortadaTurismo>
                    </PortadaWrapper>
                    <CarouselWrapp>
                      <Present>
                        <Title color="#393939"> VolvÃ© a soÃ±ar con tu prÃ³ximo viaje.</Title>
                        <PTag line="2" weight="600" color="#656565" size="1rem">Seleccionamos estas ofertas para vos, algunas con reserva flexible y otras con precios sorprendentes. Elegi la que va mejor con tus planes y compra tu prÃ³ximo paquete. </PTag>
                        <BtnIntro>Ver mas ofertas </BtnIntro>
                      </Present>
                      <Carousel 
                      responsive={responsive}
                      ssr={true}
                      >
                      {packages.map((item, index) => {
                        {console.log(item, 'el item')}
                        return(
                          <Card key={index}>
                              <CardImg image={`/${item.picture}`}>
                              <NochesCard> {item.nights}</NochesCard>
                              </CardImg>
                              <CardInfoWrap>
                                <CardTop>
                                  <TitleCard>Paquetes a {item.title}</TitleCard>
                                </CardTop>
                                <CardBottom>
                                  <PTag> Precio final por persona</PTag>
                                  <PriceCard> {item.price}</PriceCard>
                                  <DiscountCard> {item.dct} </DiscountCard>
                                </CardBottom>
                              </CardInfoWrap>
                          </Card>
                        )
                      })}
                    </Carousel>
                    </CarouselWrapp>
                </IndexWrapper>
            </Layout>
        </MainWrapper>
     );
}
 
export default Index;
