import {useState, useEffect, useRef} from 'react';
import styled from 'styled-components';
import {RiHotelBedLine, RiCarLine, RiSearch2Line} from 'react-icons/ri'
import { DatePicker } from 'antd';
import moment from 'moment';
import { Input, Select } from 'antd';

export const FilterWrapper = styled.div`
width:100%;
height:38vh;
padding: 2vw;
background:#01448a;
border-radius:5px;
display:flex;
flex-direction: column;
justify-content:space-around;
grid-row-gap:4vh;
`;

export const Options = styled.div`
width:43vw;
display:grid;
grid-template-columns:auto auto auto;
grid-column-gap: 2vw;
`;
export const Filters = styled.div`
display:grid;
width:65vw;
grid-template-columns: 30% 12% 14% 14% 18% 12%;
grid-column-gap:1vw;
`;
export const Aoptions = styled.a`
color: ${props => props.selected ? '#fd2542' : 'white'};
display: flex;
font-family:'Open Sans', sans-serif
text-align: center;
background: ${props => props.selected ? 'white' : 'none'};
border:1px solid white;
font-weight: 600;
padding: 8px;
border-radius: 5px;
justify-content:space-around;
border-radius: 30px;

`;
export const InputFrom = styled.input`
  
`;
export const InputTo = styled.input`
border: 1px solid #CCC;
border-radius: 3px;
outline: none;
padding-right: 16px;
padding-left: 16px;
height:40px;
line-height: 40px;
`;
export const P = styled.p`
  font-family:'Open Sans', sans-serif;
  font-size:1rem;
  color:white;
  font-weight:600;
  margin:0;
`;
export const LabelInput = styled.label`
  color:white;
  font-family:'Open Sans', sans-serif;
`;
export const InputPack = styled.div`
  display:grid;
  grid-template-rows: auto auto;
  grid-row-gap:1vh;
`;
export const BtnSearchFilter = styled.a`
background: white;
color:#ea1b37;
width: 10vw;
margin:0 2vw 0 4vw;
text-align: center;
font-weight: 700;
padding: 8px;
border-radius: 30px;
font-family: 'Open Sans', sans-serif;
align-self: flex-end;
`;
const FilterSearch = ( ) => {

    const fromCity = useRef(null);

    const [option, setOption] = useState('VH')
    const [chosenPack, setChosenPack] = useState("");
    const [room, setRooms] = useState("");
    const [startDate, setStartDate] = useState(new Date());
    const [endDate, setEndDate] = useState(new Date());
    const dateFormat = 'DD/MM/YYYY';

    useEffect(()=> {
        var places = require('places.js');
        var placesAutocomplete = places({
            appId: 'pl992MAS3B1L',
            apiKey: 'd964608a03f900dc27a963d7c3953c47',
            container: document.querySelector('#from-city')
          });
    }, [])

    const { Option } = Select;

    const getFrom = () => {
        let paquete;
        switch (option) {
            case 'VH':
                paquete = 'Viaje mas alojamiento';
                break;
        case 'VH2' :
            paquete = 'Viaje mas 2 alojamientos';
                break;
                case 'VA' :
                    paquete = 'Viaje mas auto';
                    break;
        }
        const desde = fromCity.current.value;
        const destino = 'La Rioja';
        const fecha = startDate;
        setChosenPack(`Elegiste el paquete ${paquete} con destino a la rioja desde ${desde} en la fecha ${fecha} con ${room}`)

    }
    const getHabitacion = (value) => {
        setRooms(value)
    }
    
  
    return(
        <>
        <FilterWrapper>
            <P>Elegí tu plan:</P>
            <Options>
                <Aoptions onClick={() => setOption('VH')} selected={option == 'VH' ? true : false}> Viaje + <RiHotelBedLine/> Alojamiento</Aoptions>
                <Aoptions onClick={() => setOption('VH2')} selected={option == 'VH2' ? true : false}> Viaje + <RiHotelBedLine/> 2 Alojamientos</Aoptions>
                <Aoptions onClick={() => setOption('VA')} selected={option == 'VA' ? true : false}> Viaje + <RiCarLine/> Auto</Aoptions>
            </Options>
            <Filters>
                <InputPack>
                <LabelInput> Origen:</LabelInput>
                <InputFrom ref={fromCity} placeholder="Desde" id="from-city" />
                </InputPack>
                <InputPack>
                <LabelInput>Destino:</LabelInput>
                <Input size="large" value="La Rioja" placeholder="Destino" />
                </InputPack>
                <InputPack>
                <LabelInput>Ida:</LabelInput>
                <DatePicker size="large" defaultValue={moment()} onChange={(date, datetostring)=> setStartDate(datetostring)} format={dateFormat} />
                </InputPack>
                <InputPack>
                <LabelInput> Vuelta:</LabelInput>
                <DatePicker size="large" defaultValue={moment()} onChange={(date, datetostring)=> setEndDate(datetostring)} format={dateFormat} />
                </InputPack>
                <InputPack>
                <LabelInput>Habitaciones:</LabelInput>
                <Select
                size="large"
                style={{ width: 200 }}
                placeholder="Habitaciones a elegir"
                onChange={getHabitacion}
                >
                    <Option value="habitacion + 1 cama">Habitación + 1 cama</Option>
                    <Option value="habitacion + 2 camas">Habitación + 2 camas</Option>
                    <Option value="2 habitaciones">2 habitaciones</Option>
                </Select>
                </InputPack>
                <BtnSearchFilter onClick={getFrom}> Buscar <RiSearch2Line/>  </BtnSearchFilter>
            </Filters>
        </FilterWrapper>
        <h4> {chosenPack} </h4>
        </>
    )
}
export default FilterSearch
