import {useState, useEffect, useRef} from 'react';
import styled from 'styled-components';

export const FilterWrapper = styled.div`
width:100%;
height:45vh;
padding: 10px;
background:purple;
border-radius:5px;
display:grid;
grid-template-rows:1fr 1fr;
grid-row-gap:4vh;
`;

export const Options = styled.div`
width:20vw;
display:flex;
justify-content:space-around;
`;
export const Filters = styled.div`
display:flex;
width:60vw;
justify-content:space-around;
`;
export const Aoptions = styled.a`
color: white;
background: #333;
padding:5px;
border-radius:5px;

`;
export const InputFrom = styled.input`
  
`;
export const InputTo = styled.input`
  
`;

const FilterSearch = ( ) => {
    return(
        <>
        <FilterWrapper>
            <Options>
                <Aoptions> Viaje + Alojamiento</Aoptions>
                <Aoptions> Viaje + 2 Alojamientos</Aoptions>
                <Aoptions> Viaje + Auto</Aoptions>
            </Options>
            <Filters>
                <InputFrom />
                <InputTo/>
                <InputTo/>
                <InputTo/>
            </Filters>
        </FilterWrapper>
        </>
    )
}
export default FilterSearch
