import {useState, useEffect, useRef} from 'react';
import styled from 'styled-components';
import {RiHotelBedLine, RiCarLine} from 'react-icons/ri'


export const FilterWrapper = styled.div`
width:100%;
height:45vh;
padding: 10px;
background:purple;
border-radius:5px;
display:flex;
flex-direction: column;
justify-content:space-around;
grid-row-gap:4vh;
`;

export const Options = styled.div`
width:40vw;
display:grid;
grid-template-columns:auto auto auto;
grid-column-gap: 2vw;
`;
export const Filters = styled.div`
display:flex;
width:60vw;
justify-content:space-around;
`;
export const Aoptions = styled.a`
color: #333333;
display: grid;
font-family:'Open Sans', sans-serif
text-align: center;
background: #fff;
font-weight: 600;
padding: 5px;
border-radius: 5px;
grid-template-columns: auto auto auto;
border-radius: 30px;

`;
export const InputFrom = styled.input`
  
`;
export const InputTo = styled.input`
  
`;

const FilterSearch = ( ) => {

    const fromCity = useRef(null);

    useEffect(()=> {
        var places = require('places.js');
        var placesAutocomplete = places({
            appId: 'pl992MAS3B1L',
            apiKey: 'd964608a03f900dc27a963d7c3953c47',
            container: document.querySelector('#from-city')
          });
    }, [])

    const getFrom = () => {
        console.log(fromCity.current.value)
    }
  
    return(
        <>
        <FilterWrapper>
            <Options>
                <Aoptions> Viaje + <RiHotelBedLine color="#333333"/> Alojamiento</Aoptions>
                <Aoptions> Viaje + <RiHotelBedLine color="#333333"/> 2 Alojamientos</Aoptions>
                <Aoptions> Viaje + <RiCarLine color="#333333"/> Auto</Aoptions>
            </Options>
            <Filters>
                <InputFrom ref={fromCity} type="search" id="from-city" placeholder="Desde" />
                <InputTo/>
                <InputTo/>
                <InputTo/>
            </Filters>
            
            <a onClick={getFrom}> GO!  </a>
        </FilterWrapper>
        </>
    )
}
export default FilterSearch
