import { useState, useEffect } from 'react';
import Layout from '../../components/Layout';
import styled from 'styled-components';
import {useRouter} from 'next/router'
 
const Paquete = ({data}) => {

    console.log(data, 'intro data')
 
    return ( 
        <MainWrapper>
            <Layout>
                <h1> Paquete </h1>
            </Layout>
        </MainWrapper>
     );
}
 
export default Paquete;




export async function getStaticPaths() {
    const res = await fetch(`https://www.lrseguridad.com.ar/turismorest/public/api/paquetepublicos`, {
        headers: {
          'Accept': 'application/json',
                    'Content-Type': 'application/json'
         }
         });
    const data = await res.json();
    console.log(data, 'da')
    return {
        paths: data.map(({tipo_producto, id_producto}) => ({
          params: {categoria: tipo_producto, id: id_producto.toString()}
        })),
        fallback: true
    }
}


export async function getStaticProps({ params }) {
const res = await fetch(`https://www.lrseguridad.com.ar/turismorest/public/api/paquetepublicos?paqueteturisticoproducto_principal=S&imagen_principal=S/${params.id}`, {
    headers: {
      'Accept': 'application/json',
                'Content-Type': 'application/json'
     }
     });
const data = await res.json();
console.log(res, 'el res')
return{
   props: {data},
   unstable_revalidate: 10
}
}

export const MainWrapper = styled.div`
  padding: 0px 0px 0px 0px;
  box-sizing: border-box;
  
`;
export const IndexWrapper = styled.div`
  padding:10% 8% 2% 8%;
  background: white;
  heigth:120vh;
  min-height:120vh;
  @media (max-width: 600px) {
  padding:50% 3% 3% 3%;  
  }
`;
