import { useState } from 'react';
import Layout from '../components/Layout';
import styled from 'styled-components';
import Carousel from 'react-multi-carousel';
import {RiArrowRightSLine, RiArrowLeftSLine} from 'react-icons/ri'
const responsive = {
  superLargeDesktop: {
    // the naming can be any, depends on you.
    breakpoint: { max: 4000, min: 3000 },
    items: 5
  },
  desktop: {
    breakpoint: { max: 3000, min: 1024 },
    items: 3
  },
  tablet: {
    breakpoint: { max: 1024, min: 601 },
    items: 3
  },
  mobile: {
    breakpoint: { max: 600, min: 0 },
    items: 1
  }
};
export const MainWrapper = styled.div`
  padding: 0px 0px 0px 0px;
  box-sizing: border-box;
  
`;
export const IndexWrapper = styled.div`
  padding:10% 8% 2% 8%;
  background: #f1f1f1;
  heigth:120vh;
  min-height:120vh;
`;
export const Titleh1 = styled.h1`
  font-family:'Open Sans', sans-serif;
`;
export const PortadaTurismo = styled.div`
  padding:10px;
  border-radius:5px;
  width:100%;
  position:relative;
  background:#ebebeb;
  height:40vh;
`;
export const Title = styled.h2`
  font-family:'Open Sans', sans-serif;
  color: ${props => props.color ? props.color : '#a1a1a1'};
  font-size:${props => props.size ? props.size : '1.8rem'};
  font-weight: ${props => props.weight ? props.weight : '600'};
  position:relative;

`;
export const PTag = styled.p`
font-family:'Open Sans', sans-serif;
line-height: ${props => props.line ? props.line : '1.2'};
font-size: ${props => props.size ? props.size : '0.8rem'};
color: ${props => props.color ? props.color : '#333'};
font-weight: ${props => props.weight ? props.weight : '500'};
`;
export const CarouselWrapp = styled.div`
padding: 5vh 0 0 0;
display:grid;
width:100%;
height:75vh;
background:#e4e4e4;
border-radius:5px;
grid-template-columns:25% 1fr;
grid-column-gap:1vw;
`;
export const Present = styled.div`
background: #e4e4e4;
width: 100%;
height: 85%;
padding: 1vh 2vw 0 2vw;
position: relative;
`;
export const BtnIntro = styled.a`
background: #fd2542;
  padding: 8px;
  border-radius: 5px;
  color: #f1f1f1;
  font-family: 'Open Sans',sans-serif;
  font-weight: 600;
  position: absolute;
  bottom: 0;
  font-size: 0.8em;
  right: 2vw;
`;
export const Card = styled.div`
cursor:pointer;
width:100%;
height:65vh;
background:#f1f1f1;
box-shadow:  5px 5px 10px #b0b0b0, 
             -5px -5px 10px #ffffff;
`;
export const CardImg = styled.div`
  width:100%;
  height:25vh;
  background-image: url('${props => props.image}');
  background-position:center;
  background-size:cover;
  position:relative;
`;
export const NochesCard = styled.p`
background: #ea1b37;
margin: 0;
color: #fff;
position: absolute;
font-size: 0.6rem;
left: 1vw;
line-height: 1.2;
bottom: -1vh;
font-weight: 600;
height: 3vh;
padding: 0.3vw;
border-radius: 5px;
`;
export const TitleCard = styled.h4`
  color: #393939;
  font-weight:700;
`;
export const PriceCard = styled.p`
  color:#333;
  font-size:0.8rem;
`;
export const DiscountCard = styled.p`
  background: #ea1b37;
  color:white;
  width:4vw;
  position:absolute;
  bottom:1vh;
  right:0.6vw;
  font-size:0.9rem;
  text-align:center;
  padding:2px;
  border-top-left-radius: 8px;
  border-bottom-left-radius: 8px;
`;
export const CardInfoWrap = styled.div`
  padding:10px;
  display:grid;
  grid-template-rows:25vh;
`;
export const CardTop = styled.div`
  
`;
export const CardBottom = styled.div`
border-top:1px solid #dfdfdf;
`;
export const Be = styled.span`
  font-size:1.4rem;
  color:#393939;
  font-weight:700;
`;
export const LeftArrow = styled.div`
border: 1px solid #b3b3b3;
position:absolute;
left:0;
cursor:pointer;
background:white;
width:50px;
margin-left:-20px;
height:50px;
border-radius:100%;
line-height: 4.2;
display:inline-block;
-webkit-box-shadow: 0px 3px 5px -2px rgba(0,0,0,0.75);
-moz-box-shadow: 0px 3px 5px -2px rgba(0,0,0,0.75);
box-shadow: 0px 3px 5px -2px rgba(0,0,0,0.75);
`;
export const RightArrow = styled.div`
border: 1px solid #b3b3b3;
position:absolute;
right:0;
cursor:pointer;
background:white;
width:50px;
margin-right:-20px;
border-radius:100%;
height:50px;
line-height: 4.2;
display:inline-block;
-webkit-box-shadow: 0px 3px 5px -2px rgba(0,0,0,0.75);
-moz-box-shadow: 0px 3px 5px -2px rgba(0,0,0,0.75);
box-shadow: 0px 3px 5px -2px rgba(0,0,0,0.75);
`;
export const ButtonContainer = styled.div`
position: absolute;
top: 55%;
text-align: center;
width: 100%;
display: flex;
justify-content: space-around;
`;
const Index = () => {

    const packages = [
      {
        title: 'Corredor del Bermejo',
        picture : 'v1.jpg',
        nights: '3 NOCHES',
        price: 12300,
        dct: '-6%'
      },
      {
        title: 'Corredor Ruta 40',
        picture : 'v2.jpg',
        nights: '3 NOCHES',
        price: 9200,
        dct: '-20%'
      },
      {
        title: 'Corredor de la Costa',
        picture : 'v3.jpg',
        nights: '7 NOCHES',
        price: 96300,
        dct: '-27%'
      },
      {
        title: 'Corredor del Cañon',
        picture : 'v4.jpg',
        nights: '3 NOCHES',
        price: 6300,
        dct: '-15%'
      },
      {
        title: 'Camino del viñedo',
        picture : 'v5.jpg',
        nights: '2 NOCHES',
        price: 10300,
        dct: '-15%'
      },
      {
        title: 'Camino del salitral',
        picture : 'v6.jpg',
        nights: '2 NOCHES',
        price: 4600,
        dct: '-5%'
      }

    ]
    const images = ['v1.jpg', 'v2.jpg', 'v3.jpg', 'v4.jpg'];
    const ButtonGroup = ({ next, previous, goToSlide, ...rest }) => {
      const { carouselState: { currentSlide } } = rest;
      return (
        <ButtonContainer>
          <LeftArrow className={currentSlide === 0 ? 'disable' : ''} onClick={() => previous()} > <RiArrowLeftSLine size="30px"/> </LeftArrow>
          <RightArrow onClick={() => next()} ><RiArrowRightSLine size="30px"/> </RightArrow>
        </ButtonContainer>
      );
    };
    return ( 
        <MainWrapper>
            <Layout>
                <IndexWrapper>
                    <PortadaTurismo>
                        <Title> Paquetes turisticos</Title>
                        <PTag weight="600"> Encuentra los mejores recorridos para conocer lugares mágicos e inolvidables de nuestra provincia.</PTag>
                        <PTag weight="600"> En el siguiente link podrás encontrar una gran variedad de paquetes de hoteles, recorridos, confiterías y más!</PTag>
                    </PortadaTurismo>
                    <CarouselWrapp>
                      <Present>
                        <Title weight="700" color="#393939" size="1.7rem"> Volvé a soñar con tu próximo viaje.</Title>
                        <PTag line="2" weight="600" color="#424242" size="0.9rem">Seleccionamos estas ofertas para vos, algunas con reserva flexible y otras con precios sorprendentes. Elegi la que va mejor con tus planes y compra tu próximo paquete. </PTag>
                        <BtnIntro>Ver mas ofertas </BtnIntro>
                      </Present>
                      <Carousel 
                      responsive={responsive}
                      ssr={true}
                      infinite={true}
                      arrows={false}
                      customButtonGroup={<ButtonGroup />}
                      >
                      {packages.map((item, index) => {
                        {console.log(item, 'el item')}
                        return(
                          <Card key={index}>
                              <CardImg image={`/${item.picture}`}>
                              <NochesCard> {item.nights}</NochesCard>
                              </CardImg>
                              <CardInfoWrap>
                                <CardTop>
                                  <TitleCard>Paquetes a {item.title}</TitleCard>
                                </CardTop>
                                <CardBottom>
                                  <PTag> Precio final por persona</PTag>
                                  <PriceCard> $ <Be>{item.price}</Be>Ars</PriceCard>
                                  <DiscountCard> {item.dct} </DiscountCard>
                                </CardBottom>
                              </CardInfoWrap>
                          </Card>
                        )
                      })}
                    </Carousel>
                    </CarouselWrapp>
                </IndexWrapper>
            </Layout>
        </MainWrapper>
     );
}
 
export default Index;
