import {useState, useEffect, useRef} from 'react';
import styled from 'styled-components';
import AlgoliaPlaces from 'algolia-places-react';


export const FilterWrapper = styled.div`
width:100%;
height:45vh;
padding: 10px;
background:purple;
border-radius:5px;
display:flex;
flex-direction: column;
justify-content:space-around;
grid-row-gap:4vh;
`;

export const Options = styled.div`
width:20vw;
display:flex;
justify-content:space-around;
`;
export const Filters = styled.div`
display:flex;
width:60vw;
justify-content:space-around;
`;
export const Aoptions = styled.a`
color: white;
background: #333;
padding:5px;
border-radius:5px;

`;
export const InputFrom = styled.input`
  
`;
export const InputTo = styled.input`
  
`;

const FilterSearch = ( ) => {

    const fromCity = useRef(null);

    const getFrom = () => {
        console.log('DESDE: ', fromCity.current );
    }
  
    return(
        <>
        <FilterWrapper>
            <Options>
                <Aoptions> Viaje + Alojamiento</Aoptions>
                <Aoptions> Viaje + 2 Alojamientos</Aoptions>
                <Aoptions> Viaje + Auto</Aoptions>
            </Options>
            <Filters>
                <InputFrom />
                <InputTo/>
                <InputTo/>
                <InputTo/>
            </Filters>
            <AlgoliaPlaces
            placesRef={fromCity}
            placeholder='Write an address here'
        
            options={{
                appId: 'pl992MAS3B1L',
                apiKey: 'd964608a03f900dc27a963d7c3953c47',
                language: 'en',
                countries: [],
                type: 'city',
                // Other options from https://community.algolia.com/places/documentation.html#options
            }}
        
            onCursorChanged={({ rawAnswer, query, suggestion, suggestonIndex }) => 
                console.log(rawAnswer, 'rawx')}
        
            onClear={() => 
                console.log('Fired when the input is cleared.')}
        
            onLimit={({ message }) => 
                console.log('Fired when you reached your current rate limit.')}
        
            onError={({ message }) => 
                console.log('Fired when we could not make the request to Algolia Places servers for any reason but reaching your rate limit.')}
            />
            <a onClick={getFrom}> GO!  </a>
        </FilterWrapper>
        </>
    )
}
export default FilterSearch
