import {useState, useEffect, useRef} from 'react';
import styled from 'styled-components';
var places = require('places.js');


export const FilterWrapper = styled.div`
width:100%;
height:45vh;
padding: 10px;
background:purple;
border-radius:5px;
display:flex;
flex-direction: column;
justify-content:space-around;
grid-row-gap:4vh;
`;

export const Options = styled.div`
width:20vw;
display:flex;
justify-content:space-around;
`;
export const Filters = styled.div`
display:flex;
width:60vw;
justify-content:space-around;
`;
export const Aoptions = styled.a`
color: white;
background: #333;
padding:5px;
border-radius:5px;

`;
export const InputFrom = styled.input`
  
`;
export const InputTo = styled.input`
  
`;

const FilterSearch = ( ) => {

    const fromCity = useRef(null);

    useEffect(()=> {
        var placesAutocomplete = places({
            appId: 'pl992MAS3B1L',
            apiKey: 'd964608a03f900dc27a963d7c3953c47',
            container: document.querySelector('#address-input')
          });
    }, [])

    const getFrom = () => {
        console.log(fromCity.current.value)
    }
  
    return(
        <>
        <FilterWrapper>
            <Options>
                <Aoptions> Viaje + Alojamiento</Aoptions>
                <Aoptions> Viaje + 2 Alojamientos</Aoptions>
                <Aoptions> Viaje + Auto</Aoptions>
            </Options>
            <Filters>
                <InputFrom />
                <InputTo/>
                <InputTo/>
                <InputTo/>
            </Filters>
            <input ref={fromCity} type="search" id="address-input" placeholder="Where are we going?" />
            <a onClick={getFrom}> GO!  </a>
        </FilterWrapper>
        </>
    )
}
export default FilterSearch
