import React,{ useState } from 'react';
import Head from 'next/head';
import Router from 'next/router';
import Link from 'next/link';
import styled from 'styled-components';
import { Select } from '@zeit-ui/react'
import { FiShoppingBag, FiSun, FiInfo, FiMessageCircle } from "react-icons/fi";
import {GoCalendar} from 'react-icons/go'
import { MdDirectionsBike, MdHome} from 'react-icons/md'
import {RiAccountPinBoxLine} from 'react-icons/ri'


export const HeaderWrapper = styled.div`
position:fixed;
padding:0% 7% 0% 7%;
width:100%;
box-sizing:border-box;
height:10vh;
background:#ea1b37;
display:grid;
z-index:1000;
grid-template-columns:18% 44% 1fr;
align-items:center;
`;
export const LogoWrapper = styled.img`
margin:0;
max-width:12vw;
`;
export const MenuWrapper = styled.div`
display:flex;
justify-content:space-around;
`;
export const ATag = styled.a`
display: grid;
justify-content: center;
color: #faf4f4;
font-weight: 600;
justify-items: center;
font-size:${props => props.size ? props.size : '0.8rem'};
font-family:'Open Sans',sans-serif;
`;
export const CarritoWrapper = styled.div`
display: flex;
justify-content: space-around;
`;
export const AuthWrapper = styled.div`
display:flex;
justify-content:space-around;
`;
 export const PTag = styled.p`
font-family:'Open Sans', sans-serif;
font-size: ${props => props.size ? props.size : '0.8rem'};
color: ${props => props.color ? props.color : '#333'};
 `;
export const SecondMenu = styled.div`
justify-content:end;
display:grid;
grid-column-gap:2vw;
grid-template-columns:6vw 7vw;
`;



const Header = () => {
 
    return (
        <React.Fragment>
        <Head>
            {/* <link rel="icon" type="image/png" sizes="32x32" href="/favicon-32x32.png" /> */}
            <link rel="stylesheet" type="text/css" href="/nprogress.css" />
            <link href="https://fonts.googleapis.com/css?family=Karla:400,700,700i&display=swap" rel="stylesheet"></link>
      
        </Head>
        <HeaderWrapper>
            <LogoWrapper src="/logo.png" />
            <MenuWrapper> 
                <Link href="/">
                    <ATag><MdHome size="20px" /> Inicio </ATag>
                </Link>
                <Link href="/actividades">
                    <ATag> <MdDirectionsBike size="20px"/> Que hacer </ATag>
                </Link>
                <Link href="/reservas">
                    <ATag><GoCalendar size="18px"/>Reservá</ATag>
                </Link>
                <Link href="/descubrir">
                    <ATag><FiSun size="18px"/>Descubrila </ATag>
                </Link>
                <Link href="/informacion">
                    <ATag><FiInfo size="18px"/>Información </ATag>
                </Link>
                <Link href="/contacto">
                    <ATag><FiMessageCircle size="18px"/> Contacto </ATag>
                </Link>
            </MenuWrapper>
            <SecondMenu>
            <AuthWrapper>
                <ATag size="0.8rem"> Ingresar</ATag>
                <RiAccountPinBoxLine size="20px" color="#f1f1f1"/>
            </AuthWrapper>
            <CarritoWrapper>
                <ATag>Mis Viajes </ATag>
                <FiShoppingBag size="20px" color="#f1f1f1"/>
            </CarritoWrapper>
            </SecondMenu>
        </HeaderWrapper>
        </React.Fragment>
      );
}
 
export default Header;
