import { useState } from 'react';
import Link from 'next/link';
import styled from 'styled-components';
import { Select } from '@zeit-ui/react'
import { FiShoppingCart, FiSun, FiInfo, FiMessageCircle } from "react-icons/fi";
import {GoCalendar} from 'react-icons/go'
import { MdDirectionsBike, MdHome} from 'react-icons/md'


export const HeaderWrapper = styled.div`
position:fixed;
padding:0% 2% 0% 2%;
width:100vw;
height:10vh;
background:#ea1b37;
display:grid;
grid-template-columns:18% 44% 14% 15% 7%;
align-items:center;
`;
export const LogoWrapper = styled.h4`
margin:0;
max-width:12vw;
`;
export const MenuWrapper = styled.div`
  display:flex;
  justify-content:space-around;
`;
export const ATag = styled.a`
display: grid;
justify-content: center;
color: #faf4f4;
font-weight: 600;
justify-items: center;
font-size:${props => props.size ? props.size : '0.8rem'};
font-family:'Open Sans',sans-serif;
`;
export const CarritoWrapper = styled.div`
display: flex;
justify-content: space-around;
`;
export const AuthWrapper = styled.div`
  display:flex;
  justify-content:space-around;
`;
 export const PTag = styled.p`
   font-family:'Open Sans', sans-serif;
   font-size: ${props => props.size ? props.size : '0.8rem'};
   color: ${props => props.color ? props.color : '#333'};
 `;
const Header = () => {
 
    return (
        <HeaderWrapper>
            <LogoWrapper> Turismo LaRioja</LogoWrapper>
            <MenuWrapper> 
                <Link href="/">
                    <ATag><MdHome size="20px" /> Inicio </ATag>
                </Link>
                <Link href="/actividades">
                    <ATag> <MdDirectionsBike size="20px"/> Que hacer </ATag>
                </Link>
                <Link href="/reservas">
                    <ATag><GoCalendar size="18px"/>Reservá</ATag>
                </Link>
                <Link href="/descubrir">
                    <ATag><FiSun size="18px"/>Descubrila </ATag>
                </Link>
                <Link href="/informacion">
                    <ATag><FiInfo size="18px"/>Información </ATag>
                </Link>
                <Link href="/contacto">
                    <ATag><FiMessageCircle size="18px"/> Contacto </ATag>
                </Link>
            </MenuWrapper>
            <div></div>
            <AuthWrapper>
                <ATag size="0.8rem"> Ingresar</ATag>
                <ATag size="0.8rem"> Registrarse</ATag>
            </AuthWrapper>
            <CarritoWrapper>
                <ATag href="#!">Ver carrito </ATag>
                <FiShoppingCart size="20px" color="#f1f1f1"/>
            </CarritoWrapper>
        </HeaderWrapper>
      );
}
 
export default Header;
