import { useState } from 'react';
import Head from 'next/head'
import styled from 'styled-components';
import { Select } from '@zeit-ui/react'
import { RiShoppingCartLine } from "react-icons/ri";


export const HeaderWrapper = styled.div`
border-top:10px solid #fe2626;

padding:0% 4% 0% 4%;
width:100vw;
height:10vh;
background:#ea1b37;
display:grid;
grid-template-columns:15% 45% 25% 15%;
align-items:center;
`;
export const LogoWrapper = styled.div`
margin:0;
width:100%;
height:100%;
background-image:url('/logo.png');
background-size:cover;
background-position:center;
color:tomato;
font-family:'Roboto',sans-serif;
`;
export const MenuWrapper = styled.div`
  display:grid;
  grid-template-columns: auto auto auto auto auto auto;
`;
export const ATag = styled.a`
color:#faf4f4;
font-weight:500;
font-size:0.9rem;
font-family:'Roboto',sans-serif;
&:hover{
    color:#bcbcbc;
}
`;
export const CarritoWrapper = styled.div`
  
`;
export const AuthWrapper = styled.div`
  
`;
 
const Header = () => {
 
    return (
        <HeaderWrapper>
            <LogoWrapper></LogoWrapper>
            <MenuWrapper> 
                <ATag href="#!"> Inicio </ATag>
                <ATag href="#!"> Que hacer </ATag>
                <ATag href="#!">Reservá</ATag>
                <ATag href="#!">Descubrila </ATag>
                <ATag href="#!">Información </ATag>
                <ATag href="#!">Contacto </ATag>
            </MenuWrapper>
            <CarritoWrapper>
                <RiShoppingCartLine size="25px" color="#f1f1f1"/>
            </CarritoWrapper>
            <AuthWrapper>

            </AuthWrapper>
        </HeaderWrapper>
      );
}
 
export default Header;
