import { useState, useEffect } from 'react';
import Layout from '../components/Layout';
import styled from 'styled-components';
import Carousel from 'react-multi-carousel';
import {RiArrowRightSLine, RiArrowLeftSLine} from 'react-icons/ri'
import CarouselOffers from '../components/CarouselOffers'
import FilterSearch from '../components/FilterSearch'


export const MainWrapper = styled.div`
  padding: 0px 0px 0px 0px;
  box-sizing: border-box;
  
`;
export const IndexWrapper = styled.div`
  padding:10% 8% 2% 8%;
  background: white;
  heigth:120vh;
  min-height:120vh;
  @media (max-width: 600px) {
  padding:50% 3% 3% 3%;  
  }
`;
export const Titleh1 = styled.h1`
  font-family:'Open Sans', sans-serif;
`;
export const PortadaTurismo = styled.div`
background-image:url('/textura.jpg');
background-size:cover;
background-position:center;
margin: 0 0 10vh 0;
padding:10px;
border-radius:5px;
width:100%;
position:relative;
height:40vh;
display:grid;
grid-template-columns:70% 15% 15%;

`;
export const Title = styled.h2`
  font-family:'Open Sans', sans-serif;
  color: ${props => props.color ? props.color : '#a1a1a1'};
  font-size:${props => props.size ? props.size : '1.8rem'};
  font-weight: ${props => props.weight ? props.weight : '600'};
  position:relative;

`;
export const PTag = styled.p`
font-family:'Open Sans', sans-serif;
line-height: ${props => props.line ? props.line : '1.2'};
font-size: ${props => props.size ? props.size : '1rem'};
color: ${props => props.color ? props.color : '#333'};
font-weight: ${props => props.weight ? props.weight : '500'};
`;

export const Be = styled.span`
  font-size:1.4rem;
  color:#393939;
  font-weight:700;
`;
export const MoveteImg = styled.img`
width: 14vw;
justify-self: center;
align-self: center;
margin: -14vh 0 0 0;
`;
export const LineasImg = styled.img`
  width:8vw;
`;
export const LineasWrapper = styled.div`
  
`;


const Index = () => {

  useEffect(()=> {
    if ("geolocation" in navigator) {
      navigator.geolocation.getCurrentPosition((position) => {
        console.log('position')
      })
    } else {
      console.log("Not Available");
    }
  }, [])

     

    const packages = [
      {
        title: 'Corredor del Bermejo',
        picture : 'v1.jpg',
        nights: '3 NOCHES',
        price: 12300,
        dct: '-6%'
      },
      {
        title: 'Corredor Ruta 40',
        picture : 'v2.jpg',
        nights: '3 NOCHES',
        price: 9200,
        dct: '-20%'
      },
      {
        title: 'Corredor de la Costa',
        picture : 'v3.jpg',
        nights: '7 NOCHES',
        price: 96300,
        dct: '-27%'
      },
      {
        title: 'Corredor del Cañon',
        picture : 'v4.jpg',
        nights: '3 NOCHES',
        price: 6300,
        dct: '-15%'
      },
      {
        title: 'Camino del viñedo',
        picture : 'v5.jpg',
        nights: '2 NOCHES',
        price: 10300,
        dct: '-15%'
      },
      {
        title: 'Camino del salitral',
        picture : 'v6.jpg',
        nights: '2 NOCHES',
        price: 4600,
        dct: '-5%'
      }

    ]

    return ( 
        <MainWrapper>
            <Layout>
                <IndexWrapper>
                    <PortadaTurismo>
                        <div>
                          <Title color="white"> Paquetes turisticos</Title>
                          <PTag color="white" weight="600"> Encuentra los mejores recorridos para conocer lugares mágicos e inolvidables de nuestra provincia.</PTag>
                          <PTag color="white" weight="600"> En el siguiente link podrás encontrar una gran variedad de paquetes de hoteles, recorridos, confiterías y más!</PTag>
                        </div>
                        <MoveteImg src="/movetev1.png"/>
                        <LineasWrapper>
                          <LineasImg src="/lineas.png"/>
                        </LineasWrapper>

                    </PortadaTurismo>
                    <FilterSearch offers={packages}/>
                    <CarouselOffers offers={packages}/>
                    <CarouselOffers offers={packages}/>
                    <CarouselOffers offers={packages}/>
                </IndexWrapper>
            </Layout>
        </MainWrapper>
     );
}
 
export default Index;
