import { useState } from 'react';
import Head from 'next/head'
import styled from 'styled-components';
import { Select } from '@zeit-ui/react'
import { FiShoppingCart } from "react-icons/fi";


export const HeaderWrapper = styled.div`

padding:0% 2% 0% 2%;
width:100vw;
height:14vh;
background:#ea1b37;
display:grid;
grid-template-columns:25% 50% 25% 25%;
`;
export const LogoWrapper = styled.img`
margin:0;
max-width:16vw;
`;
export const MenuWrapper = styled.div`
  display:flex;
  justify-content:space-around;
`;
export const ATag = styled.a`
color:#faf4f4;
font-weight:500;
font-size:${props => props.size ? props.size : '1rem'};
font-family:'Roboto',sans-serif;
&:hover{
    color:#bcbcbc;
}
`;
export const CarritoWrapper = styled.div`
  
`;
export const AuthWrapper = styled.div`
  display:flex;
  justify-content:space-around;
`;
 export const PTag = styled.p`
   font-family:'Roboto', sans-serif;
   font-size: ${props => props.size ? props.size : '0.8rem'};
   color: ${props => props.color ? props.color : '#333'};
 `;
const Header = () => {
 
    return (
        <HeaderWrapper>
            <LogoWrapper src="/logo.png" />
            <MenuWrapper> 
                <ATag href="#!"> Inicio </ATag>
                <ATag href="#!"> Que hacer </ATag>
                <ATag href="#!">Reservá</ATag>
                <ATag href="#!">Descubrila </ATag>
                <ATag href="#!">Información </ATag>
                <ATag href="#!">Contacto </ATag>
            </MenuWrapper>
            <AuthWrapper>
                <ATag size="0.8rem"> Ingresar</ATag>
                <ATag size="0.8rem"> Registrarse</ATag>
            </AuthWrapper>
            <CarritoWrapper>
                <ATag href="#!">Ver carrito </ATag>
                <FiShoppingCart size="20px" color="#f1f1f1"/>
            </CarritoWrapper>
        </HeaderWrapper>
      );
}
 
export default Header;
