import { useState } from 'react';
import Head from 'next/head'
import styled from 'styled-components';
import { Select } from '@zeit-ui/react'
import { AiOutlineShopping } from "react-icons/ai";


export const HeaderWrapper = styled.div`

padding:0% 2% 0% 2%;
width:100vw;
height:14vh;
background:#ea1b37;
display:grid;
grid-template-columns:20% 80%;
`;
export const LogoWrapper = styled.img`
margin:0;
max-width:16vw;
`;
export const MenuWrapper = styled.div`
  display:flex;
  justify-content:space-around;
`;
export const ATag = styled.a`
color:#faf4f4;
font-weight:500;
font-size:${props => props.size ? props.size : '1rem'};
font-family:'Roboto',sans-serif;
&:hover{
    color:#bcbcbc;
}
`;
export const CarritoWrapper = styled.div`
  
`;
export const AuthWrapper = styled.div`
  display:flex;
  justify-content:space-around;
`;
export const TopHeader = styled.div`
  display: grid;
  grid-template-columns:80% 20%;
`;
export const LeftHeader = styled.div`
  
`;
export const RightHeader = styled.div`
  display:grid;
  grid-template-rows:40% 60%;
`;
export const BottomHeader = styled.div`
  display:grid;
  align-items:center;
  grid-template-columns:70% 1fr;
`;
export const TopSmallWrapper = styled.div`
  display:grid;
  align-items:center;
  grid-template-columns:auto auto;
`;
 
const Header = () => {
 
    return (
        <HeaderWrapper>
            <LeftHeader>
                <LogoWrapper src="/logo.png" />
            </LeftHeader>
            <RightHeader>
                <TopHeader>
                    <div>
                    </div>
                    <TopSmallWrapper>
                    <CarritoWrapper>
                        <AiOutlineShopping size="25px" color="#f1f1f1"/>
                    </CarritoWrapper>
                    <AuthWrapper>
                        <ATag size="0.8rem"> Ingresar</ATag>
                        <ATag size="0.8rem"> Registrarse</ATag>
                    </AuthWrapper>
                    </TopSmallWrapper>
                </TopHeader>
                <BottomHeader>
                    <MenuWrapper> 
                        <ATag href="#!"> Inicio </ATag>
                        <ATag href="#!"> Que hacer </ATag>
                        <ATag href="#!">Reservá</ATag>
                        <ATag href="#!">Descubrila </ATag>
                        <ATag href="#!">Información </ATag>
                        <ATag href="#!">Contacto </ATag>
                    </MenuWrapper>
                </BottomHeader>
                </RightHeader>
        </HeaderWrapper>
      );
}
 
export default Header;
