import { useState, useEffect } from 'react';
import Layout from '../components/Layout';
import styled from 'styled-components';
import {useRouter} from 'next/router'
import Loader from 'react-loader-spinner'
 
const Ingresar = () => {

    const [isLoading, setIsLoading] = useState(false);
    const [email, setEmail] = useState("");
    const [password, setPassword] = useState("");

    const loguser = async () => {
        setIsLoading(true)
    }   
 
    return ( 
        <MainWrapper>
            <Layout>
                <Content>
                    <LoginWrapper>
                        <p> Ingreso de usuario</p>
                        <label>Email:</label>
                        <input placeholder="Ingrese un email" value={email} onChange={(e)=> setEmail(e.target.value)}/>
                        <label>Contraseña:</label>
                        <input  placeholder="Ingrese su contraseña" value={password} onChange={(e)=> setPassword(e.target.value)}/>
                        <button onClick={loguser}> Ingresar</button>
                    </LoginWrapper>
                </Content>
            </Layout>
        </MainWrapper>
     );
}
 
export default Ingresar;

export const MainWrapper = styled.div`
  padding: 0px 0px 0px 0px;
  box-sizing: border-box;
  min-height:100vh;
`;

export const Content = styled.div`
height:90vh;
position:relative;
`;
export const LoginWrapper = styled.div`
width:30vw;
padding:25px;
height:initial;
display:grid;
border-radius:5px;
border:1px solid #c5c5c5;
position:absolute;
top:57%;
left:50%;
transform:translate(-50%, -50%);
p{
    font-family:'Karla', sans-serif;
    font-weight:600;
}
input{
    font-family:'Karla', sans-serif;
    font-weight:600;
    padding:4px 6px 4px 10px;
    color:#333;
    outline:none;
    &::placeholder{
        color: #717171;
        font-weight:500;
    }
}
label{
    font-family:'Karla', sans-serif;
    font-weight:500;
}
button{
    padding:6px;
    border-radius:5px;
    background:#602b2a;
    font-family:'Karla', sans-serif;
    font-weight:600;
    border:none;
    width:12vw;
    color:white;
    justify-self:center;
    margin:3% 0 0 0;
    cursor:pointer;
}
`;
