import { useState } from 'react';
import Head from 'next/head'
import styled from 'styled-components';
import { Select } from '@zeit-ui/react'
import { AiOutlineShopping } from "react-icons/ai";


export const HeaderWrapper = styled.div`
border-top:10px solid #fe2626;
background-image:url('/logo.png');
background-size:cover;
background-position:center;
  padding:0% 4% 0% 4%;
  width:100vw;
  height:10vh;
  background:#f1f1f1;
  display:grid;
  grid-template-columns:15% 45% 25% 15%;
  align-items:center;
`;
export const LogoWrapper = styled.h4`
margin:0;
color:tomato;
font-family:'Roboto',sans-serif;
`;
export const MenuWrapper = styled.div`
  display:grid;
  grid-template-columns: auto auto auto auto auto auto;
`;
export const ATag = styled.a`
color:#747474;
font-weight:500;
font-size:0.9rem;
font-family:'Roboto',sans-serif;
&:hover{
    color:#333;
}
`;
 
const Header = () => {
 
    return (
        <HeaderWrapper>
            <LogoWrapper> Turismo La Rioja</LogoWrapper>
            <MenuWrapper> 
                <ATag href="#!"> Que hacer </ATag>
                <ATag href="#!">Reservá</ATag>
                <ATag href="#!">Descubrila </ATag>
                <ATag href="#!">Información </ATag>
                <ATag href="#!">Contacto </ATag>
                <ATag href="#!">Profesionales </ATag>
            </MenuWrapper>
            <ATag href="#!"> Carrito </ATag>
            <Select placeholder="Idioma" initialValue="español">
                <Select.Option value="español">Español</Select.Option>
                <Select.Option value="english">English</Select.Option>
            </Select>
        </HeaderWrapper>
      );
}
 
export default Header;
